// import React from 'react';
import '../App.css';
import React, { useState } from 'react';

function RegistrationInfo() {
    const [name, email, set] = useState('');
    // const [email, setEmail] = useState('');
    return (
        <form>
            <fieldset>
                <legend></legend>
                    <label>Your Name:
                        <input type="text" name ={name}
                            onChange={e => set(e.target.name)} />
                    </label>
                    <label>Your Email:
                        <input type="text" email ={email}
                            onChange={e => set(e.target.email)} />
                    </label>
            </fieldset>
            <button onClick={e => {
                set(e.target.name);
                set(e.target.email);
                alert(`Hi ${name}! Thank you for registering. We will send you promotions at ${email}`);
                e.preventDefault();
            }}>Submit</button>
        </form>
    );
}

export default RegistrationInfo;
